import React from "react";
import Layout from "../../layouts";
import { Link } from "react-router-dom";
import { Container, Row, Col } from "react-bootstrap";
import company from "../../data/company";
import parse from 'html-react-parser';
import './Index.scss'
console.log(company);
// console.log(this.props.match);
class CompanyPage extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      data: null,
    }
  }

  componentDidMount() {
    const filterData = company.filter((item) => window.location.href.split('/')[4].replace(/%20/g, ' ') == item.title);
    console.log('cari',filterData)
    if (filterData.length > 0) {
      this.setState({ data: filterData[0] }, () => {
        console.log('wewe',window.location.href.split('/')[4].replace(/%20/g, ' '));
      });
    }
  }

  componentDidUpdate(prevProps) {
    if (this.props.match.params.data != null && prevProps.match.params.data != this.props.match.params.data) {
      const filterData = company.filter((item) => this.props.match.params.data == item.title);
      if (filterData.length > 0) {
        this.setState({ data: filterData[0] }, () => {
        });
      }
    }
  }
  render() {
    const { data } = this.state;
    return (
      <Layout>
        <div className="page-title-area">
          <Container>
            <Row>
              <Col lg="12">
                <div className="page-title-item text-center">
                  <h2 className="title">
                    {data != null && data != undefined ? data.title : "Work Details"}
                  </h2>
                  <nav aria-label="breadcrumb">
                    {data != null && data != undefined ? data.subTitle : "Immigrants"}
                  </nav>
                </div>
                {/* page title */}
              </Col>
            </Row>
            {/* row */}
          </Container>
          {/* container */}
        </div>
        {/*====== PAGE TITLE PART ENDS ======*/}

        {/*====== CASE DETAILS PART START ======*/}
        <div className="case-details-area pt-120 pb-115">
          <Container>
            <Row>
              {data != null && data != undefined && data.content.map((item,index) => (
                <div className="case-content-2 pt-40 pb-25">
                <h4 className="title">{item.title}</h4>
                {parse(item.description)}
              </div>
              ))}
            </Row>
          </Container>
        </div>
      </Layout>
    );
  }
}

export default CompanyPage;
